package class9HomeWorks;

public class AnimalsArrayLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String[] anml= {"Lion "+"Tiger "+"Bear "+"Snake "+"Dog "+"Cat"};
		for(String a:anml) {
			System.out.println(a);
		}
		for(int b=0; b<anml.length; b++) {
			System.out.println(anml[b]);
		}

}
	
}
