package class9HomeWorks;

public class CarsArray2Loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
String[] carb= {"Nissan "+"Chevrolet "+"Mazda "+"Mercedes "+"Hyundai "+"Ford"};
for(String a:carb) {
	System.out.println(a);
}
for(int b=0; b<carb.length; b++) {
	System.out.println(carb[b]);
}
	}

}
