
public class doubleComparison {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double v1 = 31.3;
		double v2 = 21.3;
		
		if (v1 > v2) {
			System.out.println("Double value  " + v1 + " is greater than " + v2);

		} else {
			System.out.println("false");
		}
	}

}
